function editSettingValue(n){const i=document.getElementById(`edit-${n}`),t=document.getElementById(`form-${n}`);i&&t?(i.classList.add("d-none"),t.classList.add("d-flex"),t.classList.remove("d-none")):swal("Oups","Cannot edit setting value. Please try to reload page.","warning")}async function saveSettingValue(n){const i=document.getElementById(`input-${n}`),r=document.getElementById(`edit-${n}`),t=document.getElementById(`form-${n}`);i&&r&&t?(await postToServer("/Setting/Setting/ChangeValue","Saved",{id:n,value:i.value}),r.classList.remove("d-none"),t.classList.add("d-none"),t.classList.remove("d-flex")):swal("Oups","Cannot edit setting value. Please try to reload page.","warning")}function cancelEditing(n){const i=document.getElementById(`edit-${n}`),t=document.getElementById(`form-${n}`);i.classList.remove("d-none");t.classList.add("d-none");t.classList.remove("d-flex")}